		{"STR_ACTIVITIES_MILITARY", "Activities: Military"},
		{"STR_ACTIVITIES_ENTERPRISE", "Activities: Enterprise"},
		{"STR_ACTIVITIES_COMMERCIAL", "Activities: Commercial"},
